mapper.select:
SELECT ac.COURSE_ID AS courseId, ac.COURSE_NAME AS courseName, ac.CONTENT AS content, ac.CREATE_TIME AS createTime, ac.UPDATE_TIME AS updateTime, ac.USER_ID AS userId, ac.`WEEK` AS `week`, ac.START_TIME AS startTime, ac.END_TIME AS endTime, ac.SIGNED_TIME AS signedTime, ac.LAT AS lat, ac.LON AS lon, ac.LOCATION_NAME AS locationName, ac.SCOPE AS scope, ac.IMAGES AS images, ac.STATUS AS status, ac.COURSE_JOB_ID AS courseJobId, ac.STUDENT_JOB_ID AS studentJobId FROM APP_COURSE ac WHERE ac.COURSE_NAME = #{courseName}
AND ac.COURSE_ID != #{courseId}
LIMIT 1

SELECT ac.COURSE_ID AS courseId, ac.COURSE_NAME AS courseName, ac.CONTENT AS content, ac.USER_ID AS userId, su.FULL_NAME AS fullName, ac.`WEEK` AS `week`, ac.START_TIME AS startTime, ac.END_TIME AS endTime, ac.SIGNED_TIME AS signedTime, ac.LAT AS lat, ac.LON AS lon, ac.LOCATION_NAME AS locationName, ac.SCOPE AS scope, ac.IMAGES AS images, ac.STATUS AS STATUS, CASE WHEN `week` LIKE '%${week}%' THEN 1 ELSE 0 END AS isToday, COUNT( acs.USER_ID ) allCount FROM APP_COURSE ac LEFT JOIN SYS_USER su ON su.USER_ID = ac.USER_ID LEFT JOIN APP_COURSE_STUDENT acs ON acs.COURSE_ID = ac.COURSE_ID WHERE 1 = 1
AND su.USER_ID = #{userId}
GROUP BY ac.COURSE_ID ORDER BY isToday DESC
